mapper.select:
select * from course


        and course_name like concat('%', #{courseName}, '%')


        and teacher_name like concat('%', #{teacherName}, '%')


        and course_type = #{courseType}


        and suitable_grade = #{suitableGrade}


order by id desc
limit #{start}, #{limit}

select count(*) from course


        and course_name like concat('%', #{courseName}, '%')


        and teacher_name like concat('%', #{teacherName}, '%')


        and course_type = #{courseType}


        and suitable_grade = #{suitableGrade}
